    a.resize(n);

    FORN(i, n)
    {
        GI(a[i]);
    }

    int p;

    FORN(i, n)
    {
        GI(p);
        insert(p);
    }

    FORN(i, n)
    {
        printf("%d ", solve(a[i]));
    }
    printf("\n");

    return 0;
}

