typedef pair<int, int> pt;
#define x first
#define y second

inline int gcd(int a, int b) {
    while (b) swap(a %= b, b);
    return a;
}

struct line {
    int A, B, C;

    line(const pt &a, const pt &b) {
        int dx = a.x - b.x;
        int dy = a.y - b.y;
        A = dy;
        B = -dx;
        C = dx * a.y - dy * a.x;
        int g = gcd(gcd(abs(A), abs(B)), abs(C));
        if (A < 0 || (A == 0 && B < 0)) g = -g;
        A /= g;
        B /= g;
        C /= g;
    }

    bool operator<(const line &other) const {
        if (A != other.A) return A < other.A;
        return B == other.B ? C < other.C : B < other.B;
    }   
};

inline int det(int a, int b, int c, int d) {
 return a * d - b * c;
}

bool intersect(const line &m, const line &n, pt & res) {
 int zn = det(m.A, m.B, n.A, n.B);
 if (zn == 0) return false;
 res.x = -det(m.C, m.B, n.C, n.B);
 res.y = -det(m.A, m.C, n.A, n.C);
    if (res.x % zn || res.y % zn) return false;
    res.x /= zn;
    res.y /= zn;
 return true;
}

inline bool check(const vector<pt> &a, const pt &center) {
    map<line, int> cnt;
    for (auto i : a) {
        line t(center, i);
        // cout << t.A << ' ' << t.B << ' ' << t.C << endl;
        cnt[t] += 1;
        if (cnt.at(t) > 2) return false;
    }
    for (auto it : cnt) if (it.second != 2) return false;
    return true;
}

pt solve(vector<pt> &a) {
    int n = a.size();
    random_shuffle(a.begin(), a.end());
    set<pt> s;
    forn(i, n) s.insert(a[i]);
    for (int i = 1; i < n; ++i) {
        line cur(a.front(), a[i]);
        int l = i == 1 ? 2 : 1;
        for (int j = 2; j < n; ++j) if (j != l && j != i) {
            line other(a[l], a[j]);
            pt center;
            if (!intersect(cur, other, center) || s.count(center) || max(abs(center.x), abs(center.y)) > 1000000) continue;
            if (check(a, center)) return center;
        }
    }
    return {MOD, MOD};
}

void smain() { 
    int n;
    for (; cin >> n; ) {
        n *= 2;
        vector<pt> a(n);
        forn(i, n) cin >> a[i].x >> a[i].y;
        auto res = solve(a);
        if (res.first >= MOD) cout << "No\n";
        else cout << "Yes\n" << res.first << ' ' << res.second << endl;
    }
}

