        if (a[i] > a[j]){
            ans += qs[mid] - qs[i-1] + (mid-i+1)*a[j];
            b[k++] = a[j++];
        }
        else {
            b[k++] = a[i++];
        }
    }
    while (i <= mid) b[k++] = a[i++];
    while (j <= r)   b[k++] = a[j++];
    for (int i = l; i <= r; i++) a[i] = b[i];
}


