using namespace std;
int mx=0,idx;
int m;
int dfs(int curr,vector<bool> &vis,vector<vector<int>> &path){
	vis[curr]=true;
	int cnt=1;
	bool cycle = false;
	for(int i=0;i<path[curr].size();i++){
		int v = path[curr][i];
		if(v==m) cycle = true;
		if(vis[v]) continue;
		int k=dfs(v,vis,path);
		if(k==0) cycle = true;
		cnt+=k;
	}
	if(curr!=m){
		if(cnt>mx){
			mx = cnt;
			idx = curr;
		}
		else if(cnt==mx)
			idx = min(idx,curr);
	}
	if(cycle) return 0;
	return cnt;
}
int main(){
	cin.tie(NULL)->sync_with_stdio(false);
	int n;
	cin >> n >> m;
	vector<vector<int>> path(n+1);
