ll n,m,q;
vector<vector<ll>> dp(509,vector<ll>(509,0));
vector<vector<ll>> cnt(509,vector<ll>(509,0));
vector<ll> price;
int main(){
	ios_base::sync_with_stdio(false);cin.tie(NULL);
	cin >> n >> m >> q;
	for(int i=0;i<n;i++){
		ll x;cin >> x;
		price.push_back(x);
	}
	for(int i=1;i<=n;i++){
		for(int j=1;j<=n;j++){
			if(i==j)continue;
			dp[i][j]=dp[j][i]=price[i-1]+price[j-1];
			cnt[i][j]=cnt[j][i]=2;
		}
	}
	while(m--){
		ll x,y,z;cin >> x >> y >> z;
		dp[x][y]=dp[y][x]=min(dp[x][y],z);
		cnt[x][y]=cnt[y][x]=2;
	}
	for(int k=1;k<=n;k++){
		for(int i=1;i<=n;i++){
			for(int j=1;j<=n;j++){
				if(dp[i][j]>dp[i][k]+dp[k][j]){
					dp[i][j]=dp[i][k]+dp[k][j];
					cnt[i][j]=cnt[i][k]+cnt[k][j];
				}else if(dp[i][j]==dp[i][k]+dp[k][j]){
					cnt[i][j]=max(cnt[i][j],cnt[i][k]+cnt[k][j]);
				}
			}
		}
	}
	while(q--){
		ll st,en;cin >> st >> en;
		cout << dp[st][en] << " " << cnt[st][en] << "\n";
	}
	return 0;
