int N, L, a, b;
multiset<int> A, B, C;


int main() {
    ios_base::sync_with_stdio(0), cin.tie(0);
    cin >> N >> L >> a >> b;
    vector<int> v(N + 1);
    for (int i = 1; i <= N; i++) {
        cin >> v[i];
    }
    sort(v.begin(), v.end());
    for (int i = 1; i <= a; i++) {
        A.insert(v[i]);
    }
    for (int i = a + 1; i <= b; i++) {
        B.insert(v[i]);
    }
    for (int i = b + 1; i <= N; i++) {
        C.insert(v[i]);
    }


    while (L--) {
        int x = *A.rbegin();
        int y = *B.rbegin();
        B.erase(prev(B.end()));
        A.erase(prev(A.end()));
        B.insert(x + (y - x) / 2);
        A.insert(y - x);


        if (*A.rbegin() > *B.begin()) {
            int i = *A.rbegin();
            int j = *B.begin();
            A.insert(j);
            B.insert(i);
            A.erase(prev(A.end()));
            B.erase(B.begin());
        }
    }


    for (auto x : A) cout << x << " ";
    for (auto x : B) cout << x << " ";
    for (auto x : C) cout << x << " ";


