int isPrime(ll x)
{
 if(x==1)return 1;
 if(x==2)return 0;
 if(x%2==0)return 2;
 for(int i=3;i<=sqrt(x);i+=2)
 {
  if(x%i==0)return i;
 }
 return 0;
}

