using namespace std;

int money=INT_MAX;

void doit(int n,int k,int a,int b,int sum)
{
    if (n>1)
    {
        doit(n-1,k,a,b,sum+a);

        if (n%k==0)
            doit(n/k,k,a,b,sum+b);
    }
    else
    {
        money=money<sum?money:sum;
        return;
    }

    return;
}

int main()
{
    int n,k,a,b;

    cin>>n>>k>>a>>b;

    if (k>1)
    {
        doit(n,k,a,b,0);
        cout<<money;
    }
    else
        cout<<a*(n-1);

    return 0;
}

