int n, a[100001],b[100001],n0,n1,l,r;
int main()
{
    scanf("%d",&n);
    for (int i = 1; i <= n; i++) scanf("%d",&a[i]);
    scanf("\n");
    for (int i = 1; i <= n; i++)
    {
        char c;
        scanf("%c",&c);
        b[i] = c - 48;
    }
    n0 = 4; n1 = 0; l = -1e9; r = 1e9;
    for (int i = 5; i <= n; i++)
        if (b[i] == 1)
        {
            if (n0 == 4)
            {
                int t = -round(1e9) - 1;
                for (int j = i - 4; j <= i; j++) t = max(t,a[j]);
                t++;
                if ((t >= l) && (t <= r)) l = t;
            }
            n0 = 0; n1 = min(4,n1+1);
        }
        else
        {
            if (n1 == 4)
            {
                int  t = round(1e9) + 1;
                for (int j = i - 4; j <= i; j++) t = min(t,a[j]);
                t--;
                if ((t >= l) && (t <= r)) r = t;
            }
            n1 = 0; n0 = min(4,n0 + 1);
        }
    printf("%d %d",l,r);
    return 0;
}

