bool isprime(int n)
{
    int t=sqrt(n);
    for(int i=2;i<=t;i++)
    {
        if(n%i==0)
        {
            return false;
        }
    }
    return true;
}
int main()
{
    int n,i;
    scanf("%d",&n);
    if(isprime(n))
    {
        printf("1\n%d\n",n);
        return 0;
    }
    else if(isprime(n-2))
    {
        printf("2\n");
        printf("2 %d\n",n-2);
        return 0;
    }
    else if(isprime(n-3))
    {
         printf("2\n");
        printf("3 %d\n",n-3);
        return 0;
    }
    printf("3\n");
    n-=3;

    for(i=2;i<n;i++)
    {
        if(isprime(i)&&isprime(n-i))
        {
            break;
        }
    }
    printf("3 %d %d\n",i,n-i);
    return 0;
}

