inline int make_divs(int x) {
 sz = 0;
 while(x != 1) {
  int cd = lp[x];
  divs[sz++] = cd;
  while(x % cd == 0)
   x /= cd;
 }
}

inline int get(int i) {
 int ans = 0;
 prod[0] = 1;
 make_divs(i);
 sz = 1 << sz;
 for(int mask = 1; mask < sz; mask++) {
  prod[mask] = prod[wl[mask]] * divs[ls[mask]];
  if(ppp[mask])
   ans += cnt[prod[mask]];
  else
   ans -= cnt[prod[mask]];
 }
 return n - ans;
}

