using namespace std;
const int N = 100100;
vector<int>g[N];
vector<int>rev_g[N];
stack<int>st;
int gr[N];
bool reach_draw;
int gr_size[N];
int visited[N], n, m;
bool reach[N][2];
bool vis_scc[N];
int next_n[N][2];

void dfsx(int node) {
    if(visited[node] == 2) return;
    visited[node]++;

    if(g[node].size()==0) {
        reach[node][1] = true;
        next_n[node][1] = -1;
    }
    for(int i:g[node]) {
        if(visited[i]<2) {
            dfsx(i);
            if(reach[i][1]) {
                reach[node][0] = true;
                next_n[node][0] = i;
            }
            if(reach[i][0]) {
                reach[node][1] = true;
                next_n[node][1] = i;
            }
        }
    }
}

void dfs_scc1(int node) {
    if(vis_scc[node]) return;
    vis_scc[node] = true;
    for(int i:g[node]) {
        if(!vis_scc[i]) {
            dfs_scc1(i);
        }
    }
    st.push(node);
}
void dfs_scc2(int node, int group) {
    if(vis_scc[node])return;
    vis_scc[node] = true;
    gr[node] = group;
    for(int i:rev_g[node]) {
        if(!vis_scc[i]) dfs_scc2(i, group);
    }
}
void dfsy(int node) {
    if(visited[node]==1) return;
    visited[node] = 1;
    if(gr_size[gr[node]]>1)reach_draw = true;
    for(int i:g[node]) {
        if(!visited[i]) {
            dfsy(i);
            if(gr_size[gr[i]] > 1) reach_draw = true;
        }
    }
}

int main() {
    cin>>n>>m;
    int num_edg, v;
    for(int i=1;i<=n;i++) {
        cin>>num_edg;
        for(int j=0;j<num_edg;j++) {
            cin>>v;
            g[i].pb(v);
            rev_g[v].pb(i);
        }
    }
    int s,ss;
    cin>>s;
    ss = s;
    dfsx(s);

    if(reach[s][0]) {
        cout<<"Win\n";

        bool cc = false;
        while(next_n[s][cc] != -1) {
            cout<<s<<" ";
            s = next_n[s][cc];
            cc = !cc;
        }
        cout<<s<<"\n";
    }
    else {
        for(int i=1;i<=n;i++) {
            if(!vis_scc[i]) dfs_scc1(i);
        }

        memset(vis_scc,false,sizeof(vis_scc));
        int br = 1;
        while(!st.empty()) {
            int curr = st.top();
            st.pop();
            if(!vis_scc[curr]) {
                dfs_scc2(curr,br);
                br++;
            }
        }
        for(int i=1;i<=n;i++) {
            gr_size[gr[i]]++;
        }
        memset(visited,0,sizeof(visited));
        dfsy(ss);
        if(reach_draw) cout<<"Draw\n";
        else cout<<"Lose\n";
    }
    return 0;
}

