    while (tc--)
    {
        for (int i = 1; i <= SIZE_Y; i++)
        {
            for (int j = 1; j <= SIZE_X; j++)
            {
                d[i][j] = false;
            }
        }

        int size_x, m;
        scanf("%d%d", &size_x, &m);

