        {
            for (int x = 1; x <= size_x; x++)
            {
                printf("%d ", d[y][x]);
            }
            printf("\n");
        }
         */

        bool arrived = false;
        for (int y = 1; y <= SIZE_Y; y++)
        {
            if (table[y][size_x] != '.') continue;

            arrived |= d[y][size_x];

            for (int k = -1; k <= 1; k++)
            {
                if (y + k < 1 || y + k > SIZE_Y) continue;

                arrived |= (d[y + k][size_x - 1] & (table[y + k][size_x] == '.'));
                arrived |= (d[y + k][size_x - 2] & (table[y + k][size_x - 1] == '.' && table[y][size_x - 1] == '.'));
            }
        }
        printf("%s\n", arrived ? "YES" : "NO");
    }

    return 0;
}

