pair<long long,long long> f(vector <int> v)
{

    long long pos,neg;
    pos = neg = 0;
    ///samo ako ima negativni, togas praj ova
    bool ima_neg = false;
    bool ima_odd = false;
    bool ima_pos = false;
    for (int i = 0 ; i < v.size() ; i++)
    {
        //cout<<v[i]<<" ";
        if (v[i] <0)
            ima_neg = true;
        if (v[i]%2 != 0 )
            ima_odd = true;
        if (v[i]>0)
            ima_pos = true;
    }
    //cout<<endl;

    if (!ima_neg && !ima_pos)
        return make_pair(0,0);

    vector <int> v1;
    vector <int> v2;
    for (int i = 0 ; i< v.size() ; i++)
    {
        if (ima_odd) {
             if (v[i]%2!=0)
        {
            if (ima_pos)
                v1.push_back((v[i]-1)/2);
            if (ima_neg)
                v2.push_back((v[i]+1)/2);
        }
        else
        {
            if (ima_pos)
                v1.push_back(v[i]/2);
            if (ima_neg)
                v2.push_back(v[i]/2);
        }

        }

        else {
            v[i]/=2;
        }
    }



    if (ima_odd)
    {
        pair<long long,long long> p_min;
        int popcount_min = INT_MAX;
        if (ima_pos)
        {

            pair<long long,long long> p_pos = f(v1);
           /* if (v[0] == 1) {
                ///za 1 na prviot primer
                cout<<"p_pos: "<<p_pos.first<<" "<<p_pos.second<<endl;
            } */
            p_pos.first <<=1;
            p_pos.second <<=1;
            p_pos.first += 1;
            /*if (v[0] == 1) {
                ///za 1 na prviot primer
                cout<<"p_pos posle dodavanje: "<<p_pos.first<<" "<<p_pos.second<<endl;
            } */
            int popcount_pos = __builtin_popcount(p_pos.first) + __builtin_popcount(p_pos.second);
            if (popcount_pos < popcount_min)
            {
                p_min.first = p_pos.first;
                p_min.second = p_pos.second;
                popcount_min = popcount_pos;
            }
        }
            if (ima_neg)
            {
                pair<long long,long long> p_neg = f(v2);
                p_neg.first<<=1;
                p_neg.second<<=1;
                p_neg.second += 1;
                int popcount_neg = __builtin_popcount(p_neg.first) + __builtin_popcount(p_neg.second);
                if (popcount_neg < popcount_min)
                {
                    p_min.first = p_neg.first;
                    p_min.second = p_neg.second;
                    popcount_min = popcount_neg;
                }
            }
        return p_min;
        }


    else
    {
        pair<long long,long long> p = f(v);
        p.first <<=1;
        p.second <<=1;
        return p;
    }
}

