            a[i] = a[i - 1] + temp;
        }
    }

    int a1, a2, mx = 2000000000;
    for(int i = 0; i < n - 2; i++){
        for(int j = i; j < n - 1; j++){
            int M, N;
            int fi = a[i];
            int mid = a[j] - a[i];
            int la = a[n - 1] - a[j];
            M = max(fi,mid);
            M = max(M,la);
            N = min(fi,mid);
            N = min(N,la);
            int dif = M - N;
            if(mx > dif){
                mx = dif;
                a1 = i + 2;
                a2 = j + 2;
            }
        }    
    }        

    cout<<a1<<" "<<a2;        
    return 0;
}

